using UnityEngine;using System.Collections;public class BGM : MonoBehaviour{	public AudioClip InGame, Menu;	void OnGameStatusChange(PlayStatus last, PlayStatus current)	{		if ((int)current < (int)PlayStatus.MapGenerating)			audio.clip = Menu;		else			audio.clip = InGame;		audio.Play();	}	// Use this for initialization	void Start ()	{		GameControl.Instance.NotifyStatusChanged += OnGameStatusChange;	}		// Update is called once per frame	void Update ()	{		}}