using UnityEngine;using System.Collections;//遊戲進行狀態public enum PlayStatus{    GameTitle,    GameChooseChapter,    GameChoosePlaymode,    MapGenerating, // Map is generating    RoundScarabTurn,    ScarabTurnAnimating, // 蟲族方動畫撥放中, 無法操作    RoundHumanTurn,    HumanTurnAnimating, // 人類方動畫撥放中, 無法操作    BattleResult}public enum PlayMode{    SinglePlay,    TwoPlayer}//遊戲邏輯主控台public class GameControl{    public static GameControl _instance;    private PlayStatus _currentPlayStatus;    private PlayMode _currentPlayMode;    private ushort chapterID = 0; // 0 = no chapter    // private GUI Manager 預定地, for Sadwx    public GameControl()    {        // GUIManager.ShowGameTitle    }    public static GameControl Instance    {        get        {            if (_instance == null)                _instance = new GameControl();            return _instance;        }    }    public PlayStatus GameStatus    {        get        {            return _currentPlayStatus;        }    }    public PlayMode GameMode    {        get        {            return _currentPlayMode;        }    }    // 玩家從 title 畫面, 觸發遊戲開始    public void TriggerGameEnter()    {        if(_currentPlayStatus != PlayStatus.GameTitle)        {            DebugLog("Status error.");            return;        }        _currentPlayStatus = PlayStatus.GameChoosePlaymode;        // NGUI show ui		UIManager.Instance.Open(EnumType.UIType.Menu);    }    // 玩家從 mode 選擇觸發    public void TriggerChoosePlayMode(PlayMode mode)    {        if (_currentPlayStatus != PlayStatus.GameChoosePlaymode)        {            DebugLog("Status error.");            return;        }        _currentPlayMode = mode;        _currentPlayStatus = PlayStatus.GameChooseChapter;        // NGUI show ui		UIManager.Instance.Open(EnumType.UIType.Levels);    }    // 玩家從 關卡選擇 UI 畫面, 觸發遊戲開始    public void TriggerChooseChapter(ushort chapterID)    {        if (_currentPlayStatus != PlayStatus.GameChooseChapter)        {            DebugLog("Status error.");            return;        }        _currentPlayStatus = PlayStatus.RoundScarabTurn; //蟲族先攻        // NGUI show ui		UIManager.Instance.Open(EnumType.UIType.InGame);    }    // UI 控制區 (NGUI)    // Scene (tile, Orthella)    // update per-frame    public void Update()    {        switch(_currentPlayStatus)        {            case PlayStatus.GameChooseChapter:                //todo                break;            case PlayStatus.GameChoosePlaymode:                //todo                break;            case PlayStatus.GameTitle:                //todo                break;            case PlayStatus.MapGenerating:                //todo                break;            default:                // todo                break;        }    }    // message for debug use.    public void DebugLog(string msg)    {        Debug.Log(msg);    }}