using UnityEngine;using System.Collections;public class ChooseLevelBtnEvt : MonoBehaviour{	UIButton _button;	void Awake()	{		_button = GetComponentInChildren<UIButton>();		if (tag != TagConst.Levels[0])			_button.isEnabled = false;	}	// Use this for initialization	void Start () {		}		// Update is called once per frame	void Update () {		}	void OnClick()	{		ushort levelID;		if (ushort.TryParse(tag.Replace("Level", string.Empty), out levelID))			GameControl.Instance.TriggerChooseChapter(levelID);	}}