using UnityEngine;using System.Collections;public class ChoosePlayModeBtnEvt : MonoBehaviour{	// Use this for initialization	void Start () {		}		// Update is called once per frame	void Update () {		}	void OnClick()	{		if (tag == TagConst.SinglePlay)			GameControl.Instance.TriggerChoosePlayMode(PlayMode.SinglePlay);		else if (tag == TagConst.TwoPlayers)			GameControl.Instance.TriggerChoosePlayMode(PlayMode.TwoPlayer);		else			UIManager.Instance.Open(EnumType.UIType.Staff);	}}