using UnityEngine;using System.Collections;public class Result : MonoBehaviour{	UILabel _result;	SpringPosition _spring;	void Awake()	{		_result = GetComponentInChildren<UILabel>();		_result.text = string.Empty;	}	// Use this for initialization	void Start () {		}		// Update is called once per frame	void Update () {		}	void OnDisable()	{		transform.localPosition = new Vector3(0 , -200f, 0);	}	public void ShowResult()	{		bool isDraw = GameControl.Instance.Logic.PeopleCount == GameControl.Instance.Logic.ScarabCount;		bool isHumanWin = GameControl.Instance.Logic.PeopleCount > GameControl.Instance.Logic.ScarabCount;		if (isDraw)		{			_result.text = "Draw !";			_result.color = Color.yellow;			SpringPosition.Begin(gameObject, Vector3.zero, 1f);		}		else if (isHumanWin)		{			_result.text = "Human Win !";			_result.color = Color.red;			SpringPosition.Begin(gameObject, Vector3.zero, 1f);		}		else		{			_result.text = "Scarab Win !\n\n Colonization Success !!!";			_result.color = Color.green;			SpringPosition.Begin(gameObject, Vector3.zero, 1f);		}	}}