using UnityEngine;using System.Collections;public class UIManager : MonoBehaviour {	public GameObject Menu, Levels, InGame, Staff;	public TurnUI Turn;	static UIManager _instance;	EnumType.UIType _currentShowUI;	public static UIManager Instance	{		get {return _instance;}	}	void Awake()	{		_instance = this;	}	// Use this for initialization	void Start () 	{		Menu.SetActive(false);		Levels.SetActive(false);		InGame.SetActive(false);		Staff.SetActive(false);	}		// Update is called once per frame	void Update () {		}	void OnDestroy()	{		_instance = null;	}		public void Open(EnumType.UIType uiType)	{		_currentShowUI = uiType;		switch (uiType)		{			case EnumType.UIType.Menu:				Menu.SetActive(true);				Levels.SetActive(false);				InGame.SetActive(false);				Staff.SetActive(false);				break;			case EnumType.UIType.Levels:				Menu.SetActive(false);				Levels.SetActive(true);				InGame.SetActive(false);				Staff.SetActive(false);				break;			case EnumType.UIType.InGame:				Menu.SetActive(false);				Levels.SetActive(false);				InGame.SetActive(true);				Staff.SetActive(false);				break;			case EnumType.UIType.Staff:				Menu.SetActive(false);				Levels.SetActive(false);				InGame.SetActive(false);				Staff.SetActive(true);				break;		}	}	public void CloseStaff()	{		Staff.SetActive(false);		Open(_currentShowUI);	}}