using UnityEngine;using System.Collections;public class UIManager : MonoBehaviour {	public GameObject Menu, Levels, InGame;	static UIManager _instance;	public static UIManager Instance	{		get {return _instance;}	}	void Awake()	{		_instance = this;	}	// Use this for initialization	void Start () 	{		Menu.SetActive(false);		Levels.SetActive(false);		InGame.SetActive(false);	}		// Update is called once per frame	void Update () {		}	void OnDestroy()	{		_instance = null;	}		public void Open(EnumType.UIType uiType)	{		switch (uiType)		{			case EnumType.UIType.Menu:				Menu.SetActive(true);				Levels.SetActive(false);				InGame.SetActive(false);				break;			case EnumType.UIType.Levels:				Menu.SetActive(false);				Levels.SetActive(true);				InGame.SetActive(false);				break;			case EnumType.UIType.InGame:				Menu.SetActive(false);				Levels.SetActive(false);				InGame.SetActive(true);				break;		}	}}